import React, { Component } from 'react'
export default class person extends 
person{
constructor(props) {
    super(props);
    this.state = {
      person:{
        name:"walid",
        country:"Tunisia",
        profession : "Sales And IT Business Manager",
      },
      show:false
    };
  }
 toggle=()=>{
  this.setState({show: !this.state.show})
 }
  render() {
    return (
      <div>
        <h1>Workshop React State</h1>
        <button onClick={()=>this.toggle()}>Show/Hide</button>
        {this.state.show ?<person/> : null}
        {/* this.setState({show: !this.state}) */}
        
      </div>
    )
  }
}
